    let pc = Precalc::new(n + m + k);
    let mut ans = ModInt(0);
    let mut sub = ModInt(0);
    for z in 0..(m + k + 1) {
        sub *= ModInt(2);
        if z > m {
            sub += pc.comb(z - 1, m);
        }
        if z > k {
            sub += pc.comb(z - 1, k);
        }
        ans += pc.comb(n - 1 + z, z) * ModInt(3).pow((m + k - z) as u32) * (ModInt(2).pow(z as u32) - sub);
    }
