            other.0.cmp(&self.0)
        }
    }

    pub struct PascalsTriangle<T> {
        buf: Vec<T>,
        n: usize,
    }

    impl<T: One> PascalsTriangle<T> {
        pub fn new() -> Self {
            PascalsTriangle {
                buf: vec![T::one()],
                n: 1,
            }
        }
        pub fn get(&mut self, n: usize, m: usize) -> T {
            assert!(m <= n);
            self.ready(n);
            self.buf[n * (n + 1) / 2 + m]
        }

        pub fn ready(&mut self, n: usize) {
            while self.n <= n {
                let i = self.buf.len() - self.n;
                self.buf.push(T::one());
                for m in 1..self.n {
                    let v = self.buf[i + m - 1] + self.buf[i + m];
                    self.buf.push(v);
                }
                self.buf.push(T::one());
                self.n += 1;
            }
        }
    }

    pub struct PascalsTriangleIter<T> {
        buf: Vec<T>,
    }
    impl<T: One> PascalsTriangleIter<T> {
        pub fn new(n: usize) -> Self {
            let mut s = PascalsTriangleIter {
                buf: vec![T::one()],
            };
            for _ in 0..n {
                s.next();
            }
            s
        }
        pub fn next(&mut self) -> &[T] {
            for i in (1..self.buf.len()).rev() {
                self.buf[i] = self.buf[i] + self.buf[i - 1];
            }
            self.buf.push(T::one());
            self.get()
        }
        pub fn get(&self) -> &[T] {
            &self.buf
        }
    }

    pub trait Zero: Add<Output = Self> + Sized + Copy {
        fn zero() -> Self;
    }
    pub trait One: Zero {
        fn one() -> Self;
    }

    macro_rules! impl_zero {
        ($t:ty, $e:expr) => {
            impl Zero for $t {
                fn zero() -> Self {
                    $e
                }
            }
        }
    }
    macro_rules! impl_one {
        ($t:ty, $e1:expr, $e0:expr) => {
            impl One for $t {
                fn one() -> Self {
                    $e1
                }
            }
            impl_zero!($t, $e0);
        }
    }

    impl_one!(usize, 1, 0);
